$Service = Get-Service -n wuauservWrite-Host "Service: $($Service.Displayname)"#Schleifeif($Service.Status -eq "Stopped") {    Write-Host "STOPP"} elseif($Service.Status -eq "Running"){    Write-Host "START"} else {    Write-Host "N/A"}echo ""for($i = 0; $i -lt 10; $i++){    echo "Zahl - $i"}#$var = Read-Host -Prompt "Drücke etwas"echo $var#foreach$services = Get-Serviceforeach ( $aktuelles in $services){    echo "$($aktuelles.Name)"}#foreach als forfor($i = 0; $i -lt $services.Count; $i++){    echo "$($aktuelles.Name)"}Get-Service | % {echo $_.name}